      e.printStackTrace();
    } finally {
      bossGroup.shutdownGracefully();
      workGroup.shutdownGracefully();
    }
  }

  public EmbedHttpServer addController(Object obj) {
    this.controllerList.add(obj);
    return this;
  }
}

