import java.lang.reflect.Method;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class EmbedHttpServer {
    private final int port;
    private final DefaultController defaultController;
    private final List<Object> controllerList;
    private static final Logger LOGGER = LoggerFactory.getLogger(EmbedHttpServer.class);
    private final Map<String, Method> routeMap;

    public EmbedHttpServer(int port) {
        this.port = port;
        this.defaultController = new DefaultController();
        this.controllerList = new ArrayList<>();
        this.routeMap = new HashMap<>();
    }

    public EmbedHttpServer addController(Object obj) {
        controllerList.add(obj);
        for (Method method : obj.getClass().getMethods()) {
            if (method.isAnnotationPresent(RequestMapping.class)) {
                RequestMapping mapping = method.getAnnotation(RequestMapping.class);
                routeMap.put(mapping.value(), method);
            }
        }
        return this;
    }

    public void start(GenericFutureListener<? extends Future<? super Void>> startedListener) {
        EventLoopGroup bossGroup = new NioEventLoopGroup();
        EventLoopGroup workerGroup = new NioEventLoopGroup();
        try {
            ServerBootstrap b = new ServerBootstrap();
            b.group(bossGroup, workerGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new ChannelInitializer<SocketChannel>() {
                    @Override
                    protected void initChannel(SocketChannel ch) {
                        ch.pipeline().addLast(new HttpServerCodec());
                        ch.pipeline().addLast(new HttpObjectAggregator(65536));
                        ch.pipeline().addLast(new SimpleChannelInboundHandler<FullHttpRequest>() {
                            @Override
                            protected void channelRead0(ChannelHandlerContext ctx, FullHttpRequest request) {
                                String uri = fixUri(request.uri());
                                FullHttpResponse response = invoke(uri, ctx.channel(), request);
                                ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
                            }
                        });
                    }
                });

            ChannelFuture f = b.bind(port).sync();
            f.addListener(startedListener);
            f.channel().closeFuture().sync();
        } catch (InterruptedException e) {
            LOGGER.error("Server interrupted", e);
        } finally {
            bossGroup.shutdownGracefully();
            workerGroup.shutdownGracefully();
        }
    }

    private FullHttpResponse invoke(String uri, Channel channel, FullHttpRequest request) {
        Method method = routeMap.get(uri);
        if (method != null) {
            try {
                Object controller = method.getDeclaringClass().newInstance();
                return (FullHttpResponse) method.invoke(controller, request);
            } catch (Exception e) {
                LOGGER.error("Error invoking controller method", e);
                return new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.INTERNAL_SERVER_ERROR);
            }
        }
        return defaultController.handle(request);
    }

    private String fixUri(String uri) {
        if (uri == null || uri.isEmpty()) {
            return "/";
        }
        if (!uri.startsWith("/")) {
            uri = "/" + uri;
        }
        if (uri.length() > 1 && uri.endsWith("/")) {
            uri = uri.substring(0, uri.length() - 1);
        }
        return uri;
    }

    public static class DefaultController {
        public FullHttpResponse handle(FullHttpRequest request) {
            return new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.NOT_FOUND);
        }
    }

    public @interface RequestMapping {
        String value();
    }
}

