    private void doWrite(ByteBuffer buff, long position, long toWrite, Handler<AsyncResult<Void>> handler) {
        if (toWrite > 0) {
            writeInternal(buff, position, handler);
        } else {
            handler.handle(Future.succeededFuture());
        }
