import io.vertx.core.Handler;
import io.vertx.core.Promise;
import io.vertx.core.buffer.Buffer;
import io.vertx.core.file.OpenOptions;
import io.vertx.core.impl.ContextInternal;
import io.vertx.core.impl.VertxInternal;
import io.vertx.core.spi.metrics.FileSystemMetrics;
import java.nio.ByteBuffer;
import java.nio.channels.AsynchronousFileChannel;
import java.nio.channels.CompletionHandler;
import java.nio.file.Paths;
import java.nio.file.StandardOpenOption;
import java.util.concurrent.atomic.AtomicBoolean;

public class AsyncFileImpl implements AsyncFile {
  private final VertxInternal vertx;
  private final AsynchronousFileChannel ch;
  private final ContextInternal context;
  private final AtomicBoolean closed = new AtomicBoolean();
  private long writePos;
  private int readBufferSize = 8192;
  private Handler<Buffer> handler;
  private Handler<Void> endHandler;
  private long readPos;
  private long readLength = Long.MAX_VALUE;

  public AsyncFileImpl(VertxInternal vertx, String path, OpenOptions options, ContextInternal context) throws Exception {
    this.vertx = vertx;
    this.context = context;
    this.ch = AsynchronousFileChannel.open(Paths.get(path), optionsToOpenOptions(options));
  }

  private static Set<StandardOpenOption> optionsToOpenOptions(OpenOptions options) {
    Set<StandardOpenOption> openOptions = new HashSet<>();
    if (options.isRead()) openOptions.add(StandardOpenOption.READ);
    if (options.isWrite()) openOptions.add(StandardOpenOption.WRITE);
    if (options.isCreate()) openOptions.add(StandardOpenOption.CREATE);
    if (options.isCreateNew()) openOptions.add(StandardOpenOption.CREATE_NEW);
    if (options.isDeleteOnClose()) openOptions.add(StandardOpenOption.DELETE_ON_CLOSE);
    if (options.isTruncateExisting()) openOptions.add(StandardOpenOption.TRUNCATE_EXISTING);
    if (options.isSparse()) openOptions.add(StandardOpenOption.SPARSE);
    return openOptions;
  }

  @Override
  public Future<Void> close() {
    Promise<Void> promise = Promise.promise();
    context.runOnContext(v -> closeInternal(promise));
    return promise.future();
  }

  private void closeInternal(Promise<Void> handler) {
    if (closed.compareAndSet(false, true)) {
      try {
        ch.close();
        handler.complete();
      } catch (Exception e) {
        handler.fail(e);
      }
    } else {
      handler.complete();
    }
  }

  @Override
  public Future<Buffer> read(Buffer buffer, int offset, long position, int length) {
    Promise<Buffer> promise = Promise.promise();
    context.runOnContext(v -> doRead(buffer, offset, ByteBuffer.allocate(length), position, promise));
    return promise.future();
  }

  private void doRead(Buffer writeBuff, int offset, ByteBuffer buff, long position, Promise<Buffer> promise) {
    check();
    ch.read(buff, position, null, new CompletionHandler<Integer, Void>() {
      @Override
      public void completed(Integer result, Void attachment) {
        if (result == -1) {
          promise.complete(writeBuff);
        } else {
          buff.flip();
          writeBuff.setBytes(offset, buff);
          promise.complete(writeBuff);
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        promise.fail(exc);
      }
    });
  }

  @Override
  public Future<Void> write(Buffer buffer, long position) {
    Promise<Void> promise = Promise.promise();
    context.runOnContext(v -> doWrite(buffer.getByteBuf().nioBuffer(), position, buffer.length(), promise));
    return promise.future();
  }

  private void doWrite(ByteBuffer buff, long position, long toWrite, Promise<Void> promise) {
    check();
    writeInternal(buff, position, ar -> {
      if (ar.succeeded()) {
        promise.complete();
      } else {
        promise.fail(ar.cause());
      }
    });
  }

  private void writeInternal(ByteBuffer buff, long position, Handler<AsyncResult<Void>> handler) {
    ch.write(buff, position, null, new CompletionHandler<Integer, Void>() {
      @Override
      public void completed(Integer result, Void attachment) {
        if (buff.hasRemaining()) {
          writeInternal(buff, position + result, handler);
        } else {
          handler.handle(Future.succeededFuture());
        }
      }

      @Override
      public void failed(Throwable exc, Void attachment) {
        handler.handle(Future.failedFuture(exc));
      }
    });
  }

  private void check() {
    if (closed.get()) {
      throw new IllegalStateException("File is closed");
    }
  }
}

