    public AbstractHttp2ServerTransport(ServerTransportConfig transportConfig) {
        this.transportConfig = transportConfig;
        this.bizGroup = new NioEventLoopGroup();
        this.serverBootstrap = new ServerBootstrap();
    }

    public boolean start() {
        try {
            SelfSignedCertificate ssc = new SelfSignedCertificate();
            sslContext = SslContextBuilder.forServer(ssc.certificate(), ssc.privateKey()).build();

            serverBootstrap.group(bizGroup)
                .channel(NioServerSocketChannel.class)
                .childHandler(new Http2ServerInitializer(sslContext))
                .bind(transportConfig.getHost(), transportConfig.getPort()).sync();

            LOGGER.info("Server started on {}:{}", transportConfig.getHost(), transportConfig.getPort());
            return true;
        } catch (Exception e) {
            LOGGER.error("Failed to start server", e);
            return false;
        }
    }

    public void stop() {
        try {
            bizGroup.shutdownGracefully().sync();
