import io.netty.bootstrap.ServerBootstrap;
import io.netty.channel.*;
import io.netty.channel.nio.NioEventLoopGroup;
import io.netty.channel.socket.nio.NioServerSocketChannel;
import java.util.concurrent.atomic.AtomicInteger;

public class NettyServer extends AbstractServer {
    private final NioEventLoopGroup bossGroup;
    private final NioEventLoopGroup workerGroup;
    private Channel serverChannel;
    private final MessageHandler messageHandler;
    private final StandardThreadExecutor standardThreadExecutor;
    private final AtomicInteger rejectCounter;
    private final NettyServerChannelManage channelManage;

    public NettyServer(URL url, MessageHandler messageHandler) {
        super(url);
        this.messageHandler = messageHandler;
        this.bossGroup = new NioEventLoopGroup(1);
        this.workerGroup = new NioEventLoopGroup(url.getIntParameter("workerThreads", 4));
        this.standardThreadExecutor = new StandardThreadExecutor(url);
        this.rejectCounter = new AtomicInteger(0);
        this.channelManage = new NettyServerChannelManage();
    }

    public boolean open() {
        try {
            ServerBootstrap bootstrap = new ServerBootstrap();
            bootstrap.group(bossGroup, workerGroup)
                     .channel(NioServerSocketChannel.class)
                     .childHandler(new ChannelInitializer<Channel>() {
                         @Override
                         protected void initChannel(Channel ch) throws Exception {
                             ChannelPipeline pipeline = ch.pipeline();
                             // Add handlers for processing requests
                             pipeline.addLast(new MessageHandlerAdapter(messageHandler, standardThreadExecutor, rejectCounter));
                         }
                     })
                     .option(ChannelOption.SO_BACKLOG, 128)
                     .childOption(ChannelOption.SO_KEEPALIVE, true);

            ChannelFuture future = bootstrap.bind(url.getPort()).sync();
            serverChannel = future.channel();
            return true;
        } catch (Exception e) {
            throw new RuntimeException("Failed to bind server", e);
        }
    }

    public void close() {
        try {
            if (serverChannel != null) {
                serverChannel.close().sync();
            }
            workerGroup.shutdownGracefully().sync();
            bossGroup.shutdownGracefully().sync();
            standardThreadExecutor.shutdown();
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    public boolean isBound() {
        return serverChannel != null && serverChannel.isActive();
    }

    @Override
    public Response request(Request request) {
        throw new MotanFrameworkException("NettyServer does not support direct request handling.");
    }

    public class MessageHandlerAdapter extends SimpleChannelInboundHandler<Object> {
        private final MessageHandler messageHandler;
        private final StandardThreadExecutor executor;
        private final AtomicInteger rejectCounter;

        public MessageHandlerAdapter(MessageHandler messageHandler, StandardThreadExecutor executor, AtomicInteger rejectCounter) {
            this.messageHandler = messageHandler;
            this.executor = executor;
            this.rejectCounter = rejectCounter;
        }

        @Override
        protected void channelRead0(ChannelHandlerContext ctx, Object msg) throws Exception {
            if (!executor.execute(new Runnable() {
                @Override
                public void run() {
                    // Process the message
                    try {
                        messageHandler.handle(msg);
                    } catch (Exception e) {
                        rejectCounter.incrementAndGet();
                    }
                }
            })) {
                // Could not accept task, handle rejection
                rejectCounter.incrementAndGet();
            }
        }
    }
}
