public class HttpTestServer {
    private final int port;
    private final boolean fail;
    private final BiConsumer<HttpRequest, JsonGenerator> requestHandler;
    private final Map<String, String> headers;
    private Channel channel;
    private final NioEventLoopGroup group;
    private final List<String> responses;
    private static final JsonFactory JSON_FACTORY = new JsonFactory();

    public HttpTestServer(int port, boolean fail, BiConsumer<HttpRequest, JsonGenerator> requestHandler, Map<String, String> headers) {
        this.port = port;
        this.fail = fail;
        this.requestHandler = requestHandler;
        this.headers = headers;
        this.group = new NioEventLoopGroup();
        this.responses = new CopyOnWriteArrayList<>();
    }

    public HttpTestServer(int port, boolean fail, BiConsumer<HttpRequest, JsonGenerator> requestHandler) {
        this(port, fail, requestHandler, null);
    }

    public void run() throws InterruptedException {
        ServerBootstrap b = new ServerBootstrap();
        b.group(group)
         .channel(NioServerSocketChannel.class)
         .childHandler(new ChannelInitializer<SocketChannel>() {
             @Override
             protected void initChannel(SocketChannel ch) {
                 ch.pipeline().addLast(new HttpServerCodec());
                 ch.pipeline().addLast(new HttpObjectAggregator(65536));
                 ch.pipeline().addLast(new HttpTestServerHandler());
             }
         });

        ChannelFuture f = b.bind(port).sync();
        channel = f.channel();
    }

    public int boundPort() {
        return ((InetSocketAddress) channel.localAddress()).getPort();
    }

    public void shutDown() {
        if (channel != null) {
            channel.close();
        }
        group.shutdownGracefully();
    }

    private class HttpTestServerHandler extends SimpleChannelInboundHandler<FullHttpRequest> {
        @Override
        protected void channelRead0(ChannelHandlerContext ctx, FullHttpRequest request) {
            FullHttpResponse response;
            if (fail) {
                response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.INTERNAL_SERVER_ERROR);
            } else {
                StringWriter writer = new StringWriter();
                try (JsonGenerator generator = JSON_FACTORY.createGenerator(writer)) {
                    requestHandler.accept(request, generator);
                } catch (Exception e) {
                    response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.INTERNAL_SERVER_ERROR);
                    ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
                    return;
                }
                byte[] jsonBytes = writer.toString().getBytes();
                response = new DefaultFullHttpResponse(HttpVersion.HTTP_1_1, HttpResponseStatus.OK, Unpooled.wrappedBuffer(jsonBytes));
                response.headers().set(HttpHeaderNames.CONTENT_TYPE, "application/json");
                response.headers().set(HttpHeaderNames.CONTENT_LENGTH, jsonBytes.length);
                if (headers != null) {
                    headers.forEach(response.headers()::set);
                }
                responses.add(writer.toString());
            }
            ctx.writeAndFlush(response).addListener(ChannelFutureListener.CLOSE);
        }
    }
}

