public class WebConfigContainer {
    private final EventLoopGroup serverBossGroup;
    private final EventLoopGroup serverWorkerGroup;
    private final Logger logger;
    private final String bindAddress;
    private final int port;

    public WebConfigContainer(String bindAddress, int port) {
        this.serverBossGroup = new NioEventLoopGroup(1);
        this.serverWorkerGroup = new NioEventLoopGroup();
        this.logger = LoggerFactory.getLogger(WebConfigContainer.class);
        this.bindAddress = bindAddress;
        this.port = port;
    }

    public void start() {
        try {
