import java.net.InetSocketAddress;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;

public class GeyserServer {
    private static final Transport TRANSPORT = compatibleTransport();
    private static final int SHUTDOWN_QUIET_PERIOD_MS = 100;
    private static final int SHUTDOWN_TIMEOUT_MS = 1000;

    private final GeyserImpl geyser;
    private final EventLoopGroup group;
    private final EventLoopGroup childGroup;
    private final ServerBootstrap bootstrap;
    private final EventLoopGroup playerGroup;
    private final ChannelFuture[] bootstrapFutures;

    public GeyserServer(GeyserImpl geyser, int threadCount) {
        this.geyser = geyser;
        this.group = TRANSPORT.eventLoopGroupFactory().apply(threadCount, new DefaultThreadFactory("parent-group"));
        this.childGroup = TRANSPORT.eventLoopGroupFactory().apply(threadCount, new DefaultThreadFactory("child-group"));
        this.playerGroup = TRANSPORT.eventLoopGroupFactory().apply(threadCount, new DefaultThreadFactory("player-group"));
        this.bootstrap = createBootstrap();
        this.bootstrapFutures = new ChannelFuture[1];
    }

    public CompletableFuture<Void> bind(InetSocketAddress address) {
        CompletableFuture<Void> future = new CompletableFuture<>();
        bootstrapFutures[0] = bootstrap.bind(address).addListener((ChannelFuture f) -> {
            if (f.isSuccess()) {
                future.complete(null);
            } else {
                future.completeExceptionally(f.cause());
            }
        });
        return future;
    }

    public void shutdown() {
        for (ChannelFuture future : bootstrapFutures) {
            if (future != null) {
                future.channel().close().awaitUninterruptibly();
            }
        }
        group.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS).awaitUninterruptibly();
        childGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS).awaitUninterruptibly();
        playerGroup.shutdownGracefully(SHUTDOWN_QUIET_PERIOD_MS, SHUTDOWN_TIMEOUT_MS, TimeUnit.MILLISECONDS).awaitUninterruptibly();
    }

    private ServerBootstrap createBootstrap() {
        return new ServerBootstrap()
                .group(group, childGroup)
                .channel(TRANSPORT.channelClass());
    }

    private static Transport compatibleTransport() {
        if (IoUring.isAvailable()) {
            return new Transport(IoUringEventLoopGroup::new, IoUringDatagramChannel.class);
        } else if (Epoll.isAvailable()) {
            return new Transport(EpollEventLoopGroup::new, EpollDatagramChannel.class);
        } else if (KQueue.isAvailable()) {
            return new Transport(KQueueEventLoopGroup::new, KQueueDatagramChannel.class);
        } else {
            return new Transport(NioEventLoopGroup::new, NioDatagramChannel.class);
        }
    }

    private static boolean isClassAvailable(String className) {
        try {
            Class.forName(className, false, GeyserServer.class.getClassLoader());
            return true;
        } catch (ClassNotFoundException e) {
            return false;
        }
    }

    private static record Transport(java.util.function.BiFunction<Integer, DefaultThreadFactory, EventLoopGroup> eventLoopGroupFactory, Class<? extends DatagramChannel> channelClass) {}
}

