import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Optional;
import com.fasterxml.jackson.databind.ObjectMapper;

public class BeaconClient {
    private final int port;
    private final String token;
    private final HttpClient httpClient;
    private final ObjectMapper objectMapper;

    public BeaconClient(int port, String token) {
        this.port = port;
        this.token = token;
        this.httpClient = HttpClient.newHttpClient();
        this.objectMapper = new ObjectMapper();
    }

    public <RES> RES performRequest(BeaconInterface<?> prov, String rawNode) throws Exception {
        String url = "http://localhost:" + port + "/" + rawNode;
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(new URI(url))
                .header("Content-Type", "application/json");

        if (token != null && !token.isEmpty()) {
            requestBuilder.header("Authorization", "Bearer " + token);
        }

        HttpRequest request = requestBuilder.POST(HttpRequest.BodyPublishers.ofString(objectMapper.writeValueAsString(prov)))
                .build();

        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        return handleResponse(response);
    }

    public <REQ, RES> RES performRequest(REQ req) throws Exception {
        String url = "http://localhost:" + port + "/" + req.getClass().getSimpleName().toLowerCase();
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(new URI(url))
                .header("Content-Type", "application/json");

        if (token != null && !token.isEmpty()) {
            requestBuilder.header("Authorization", "Bearer " + token);
        }

        HttpRequest request = requestBuilder.POST(HttpRequest.BodyPublishers.ofString(objectMapper.writeValueAsString(req)))
                .build();

        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        return handleResponse(response);
    }

