import com.google.gson.JsonElement;
import com.google.gson.JsonParser;
import java.io.IOException;

public class Network {
    private static final HttpClient httpClient = HttpClient.newHttpClient();

    public HttpResponse<String> makePOSTRequest(String url, Map<Object, Object> parameters, Map<String, String> headers, boolean convertParametersToJSON) throws IOException, InterruptedException {
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .headers(headers.entrySet().stream().flatMap(e -> Map.of(e.getKey(), e.getValue()).entrySet().stream()).toArray(String[]::new));

        if (convertParametersToJSON) {
            String json = new com.google.gson.Gson().toJson(parameters);
            requestBuilder.POST(HttpRequest.BodyPublishers.ofString(json))
                    .header("Content-Type", "application/json");
        } else {
            requestBuilder.POST(buildFormDataFromMap(parameters))
                    .header("Content-Type", "application/x-www-form-urlencoded");
        }

        HttpRequest request = requestBuilder.build();
        return httpClient.send(request, BodyHandlers.ofString());
    }

    private HttpRequest.BodyPublisher buildFormDataFromMap(Map<Object, Object> data) {
        String formData = data.entrySet().stream()
                .map(entry -> entry.getKey() + "=" + entry.getValue())
                .collect(Collectors.joining("&"));
        return HttpRequest.BodyPublishers.ofString(formData);
    }

    public JsonElement makeJsonRequest(String url) throws IOException, InterruptedException {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(url))
                .GET()
                .build();

        HttpResponse<String> response = httpClient.send(request, BodyHandlers.ofString());
        if (failure(response.statusCode())) {
            throw new IOException("Request failed with status code: " + response.statusCode());
        }

        return JsonParser.parseString(response.body());
    }

    private boolean failure(int statusCode) {
        return statusCode < 200 || statusCode >= 300;
    }
}

