import java.io.IOException;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.TimeUnit;

public class Scraper {
    private OkHttpClient client;

    public Scraper() {
        this.client = new OkHttpClient.Builder()
                .retryOnConnectionFailure(true)
                .build();
    }

    public List<String> scrape(String url, long timeoutMillis) {
        OkHttpClient clientWithTimeout = client.newBuilder()
                .callTimeout(timeoutMillis, TimeUnit.MILLISECONDS)
                .build();
        return executeRequest(clientWithTimeout, new Request.Builder().url(url).build());
    }

    public List<String> scrape(String url, String user, String password, long timeoutMillis) {
        OkHttpClient clientWithAuth = client.newBuilder()
                .authenticator(new Authenticator() {
                    @Override
                    public Request authenticate(Route route, Response response) throws IOException {
                        String credential = Credentials.basic(user, password);
                        return response.request().newBuilder().header("Authorization", credential).build();
                    }
                })
                .callTimeout(timeoutMillis, TimeUnit.MILLISECONDS)
                .build();
        return executeRequest(clientWithAuth, new Request.Builder().url(url).build());
    }

    private List<String> executeRequest(OkHttpClient client, Request request) {
        List<String> result = new ArrayList<>();
        try (Response response = client.newCall(request).execute()) {
            if (response.isSuccessful() && response.body() != null) {
                result.add(response.body().string());
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        return result;
    }
}

