    }

    public void setProcessors(NIOProcessor[] processors) {
        this.processors = processors;
    }

    public void postConnect(BackendConnection c) {
        connectQueue.offer(c);
        selector.wakeup();
    }

    public void run() {
        while (true) {
            try {
                connect(selector);
                int selected = selector.select();
                if (selected > 0) {
                    Iterator<SelectionKey> iter = selector.selectedKeys().iterator();
                    while (iter.hasNext()) {
                        SelectionKey key = iter.next();
                        iter.remove();
                        if (key.isValid() && key.isConnectable()) {
                            finishConnect(key, key.attachment());
                        }
                    }
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void connect(Selector selector) throws IOException {
        BackendConnection c;
        while ((c = connectQueue.poll()) != null) {
            SocketChannel channel = SocketChannel.open();
            channel.configureBlocking(false);
            channel.connect(new InetSocketAddress(c.getHost(), c.getPort()));
            channel.register(selector, SelectionKey.OP_CONNECT, c);
        }
    }

    private void finishConnect(SelectionKey key, Object att) {
        SocketChannel channel = (SocketChannel) key.channel();
        try {
            if (channel.finishConnect()) {
                BackendConnection connection = (BackendConnection) att;
                clearSelectionKey(key);
                NIOProcessor processor = nextProcessor();
                processor.addConnection(connection);
            }
        } catch (IOException e) {
            clearSelectionKey(key);
            e.printStackTrace();
        }
    }

    private void clearSelectionKey(SelectionKey key) {
        if (key != null) {
            key.cancel();
        }
    }

    private NIOProcessor nextProcessor() {
        NIOProcessor processor = processors[nextProcessor];
        nextProcessor = (nextProcessor + 1) % processors.length;
        return processor;
    }

    public static class ConnectIdGenerator {
        private static long id = 0;

        public synchronized static long nextId() {
            return id++;
        }
    }
}

