import io.netty.channel.socket.nio.NioServerSocketChannel;
import io.netty.handler.ssl.SslContext;
import io.netty.handler.ssl.SslHandler;
import io.netty.util.concurrent.GlobalEventExecutor;

import javax.net.ssl.SSLSession;
import java.net.InetSocketAddress;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.atomic.AtomicBoolean;

public abstract class BaseHttp2Server {

    private final SslContext sslContext;
    private final AtomicBoolean hasReleasedSslContext;
    private final ServerBootstrap bootstrap;
    private final boolean shouldShutDownEventLoopGroup;
    private final ChannelGroup allChannels;

    public BaseHttp2Server(SslContext sslContext, EventLoopGroup eventLoopGroup) {
        this.sslContext = sslContext;
        this.hasReleasedSslContext = new AtomicBoolean(false);
        this.bootstrap = new ServerBootstrap();
        this.allChannels = new DefaultChannelGroup(GlobalEventExecutor.INSTANCE);

        if (eventLoopGroup == null) {
            this.bootstrap.group(new NioEventLoopGroup());
            this.shouldShutDownEventLoopGroup = true;
        } else {
            this.bootstrap.group(eventLoopGroup);
            this.shouldShutDownEventLoopGroup = false;
        }

        this.bootstrap.channel(NioServerSocketChannel.class);
        this.bootstrap.childHandler(new ChannelInitializer<SocketChannel>() {
            @Override
            protected void initChannel(SocketChannel ch) throws Exception {
                ChannelPipeline pipeline = ch.pipeline();
                if (sslContext != null) {
                    SslHandler sslHandler = sslContext.newHandler(ch.alloc());
                    pipeline.addLast(sslHandler);
                    SSLSession sslSession = sslHandler.engine().getSession();
                    addHandlersToPipeline(sslSession, pipeline);
                } else {
                    addHandlersToPipeline(null, pipeline);
                }
                allChannels.add(ch);
            }
        });
    }

    protected abstract void addHandlersToPipeline(SSLSession sslSession, ChannelPipeline pipeline);

    public CompletableFuture<Integer> start(int port) {
        CompletableFuture<Integer> futurePort = new CompletableFuture<>();
        bootstrap.bind(new InetSocketAddress(port)).addListener((ChannelFutureListener) future -> {
            if (future.isSuccess()) {
                Channel channel = future.channel();
                allChannels.add(channel);
                futurePort.complete(((InetSocketAddress) channel.localAddress()).getPort());
            } else {
                futurePort.completeExceptionally(future.cause());
            }
        });
        return futurePort;
    }

    public CompletableFuture<Void> shutdown() {
        CompletableFuture<Void> shutdownFuture = new CompletableFuture<>();
        allChannels.close().addListener((ChannelFutureListener) closeFuture -> {
            if (closeFuture.isSuccess()) {
                shutdownFuture.complete(null);
            } else {
                shutdownFuture.completeExceptionally(closeFuture.cause());
            }
        });

        if (shouldShutDownEventLoopGroup) {
            bootstrap.config().group().shutdownGracefully();
        }

        if (hasReleasedSslContext.compareAndSet(false, true)) {
            if (sslContext != null) {
                sslContext.release();
            }
        }

        return shutdownFuture;
    }
}

