import java.io.File;
import java.io.InputStream;
import java.io.OutputStream;
import java.io.IOException;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class ProcessRunner {
    private final File processRoot;

    public ProcessRunner(File processRoot) {
        this.processRoot = processRoot;
    }

    public void execute(ProcessBuilder pb) throws IOException, InterruptedException {
        pb.directory(processRoot);
        pb.redirectErrorStream(true);
        Process process = pb.start();

        ExecutorService executor = Executors.newFixedThreadPool(2);
        executor.submit(new AsyncPipe(process.getInputStream(), System.out));
        executor.submit(new AsyncPipe(process.getErrorStream(), System.err));

        int exitCode = process.waitFor();
        executor.shutdown();
        if (exitCode != 0) {
            throw new RuntimeException("Process exited with code: " + exitCode);
        }
    }

    private static class AsyncPipe implements Runnable {
        private final InputStream is;
        private final OutputStream os;
        private final boolean closeStreams;

        public AsyncPipe(InputStream is, OutputStream os) {
            this(is, os, true);
        }

        public AsyncPipe(InputStream is, OutputStream os, boolean closeStreams) {
            this.is = is;
            this.os = os;
            this.closeStreams = closeStreams;
        }

        @Override
        public void run() {
            try {
                byte[] buffer = new byte[1024];
                int length;
                while ((length = is.read(buffer)) != -1) {
                    os.write(buffer, 0, length);
                }
            } catch (IOException e) {
                e.printStackTrace();
            } finally {
                if (closeStreams) {
                    try {
                        is.close();
                        os.close();
                    } catch (IOException e) {
                        e.printStackTrace();
                    }
                }
            }
        }
    }
}

