    protected static final String CMD_SHELL = "cmd.exe";

    public ProcessResult executeScript(TaskContext context, String scriptPath) {
        try {
            String shellCommand = getRunCommand();
            ProcessBuilder processBuilder = new ProcessBuilder(shellCommand, scriptPath);
            Process process = processBuilder.start();

            StringBuilder outputBuilder = new StringBuilder();
            StringBuilder errorBuilder = new StringBuilder();

            Charset charset = getCharset();
            OmsLogger omsLogger = context.getOmsLogger();

            POOL.execute(() -> copyStream(process.getInputStream(), outputBuilder, omsLogger, charset));
            POOL.execute(() -> copyStream(process.getErrorStream(), errorBuilder, omsLogger, charset));

            boolean finished = process.waitFor(60, TimeUnit.SECONDS);
            if (!finished) {
                process.destroyForcibly();
                throw new RuntimeException("Process timeout");
            }

            int exitCode = process.exitValue();
            return new ProcessResult(exitCode, outputBuilder.toString(), errorBuilder.toString());

        } catch (Exception ex) {
            throw new RuntimeException("Error executing script", ex);
        }
    }

    private void copyStream(InputStream is, StringBuilder sb, OmsLogger omsLogger, Charset charset) {
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(is, charset))) {
            String line;
            while ((line = reader.readLine()) != null) {
                sb.append(line).append(System.lineSeparator());
                omsLogger.log(line);
            }
        } catch (Exception ex) {
            omsLogger.log("Error reading stream: " + ex.getMessage());
        }
    }

