public class PerfMonitor {
    private String sampleFrequency;
    private File perfBinData;
    private File perfParsedData;
    private Collection<String> requestedEventNames;

    public PerfMonitor(String sampleFrequency, File perfBinData, File perfParsedData, Collection<String> requestedEventNames) {
        this.sampleFrequency = sampleFrequency;
        this.perfBinData = perfBinData;
        this.perfParsedData = perfParsedData;
        this.requestedEventNames = requestedEventNames;
    }

    public void parseEvents() throws IOException, InterruptedException {
        List<String> command = Arrays.asList("perf", "script", "-i", perfBinData.getAbsolutePath());
        ProcessBuilder processBuilder = new ProcessBuilder(command);
        processBuilder.redirectErrorStream(true);
        Process process = processBuilder.start();

        try (InputStream inputStream = process.getInputStream();
             OutputStream outputStream = new FileOutputStream(perfParsedData)) {
            InputStreamDrainer drainer = new InputStreamDrainer(inputStream, outputStream);
            drainer.start();
            drainer.join();
        }

        int exitCode = process.waitFor();
        if (exitCode != 0) {
            throw new RuntimeException("Perf script process failed with exit code: " + exitCode);
        }
    }

    public Collection<String> addJVMInvokeOptions(BenchmarkParams params) {
        List<String> options = new ArrayList<>();
        options.add("perf");
        options.add("record");
        options.add("-F");
        options.add(sampleFrequency);
        options.add("-o");
        options.add(perfBinData.getAbsolutePath());

        for (String eventName : requestedEventNames) {
            options.add("-e");
            options.add(eventName);
        }

        options.add("--");
        options.addAll(params.getJvmArgs());
        return options;
    }

    private static class InputStreamDrainer extends Thread {
        private final InputStream inputStream;
        private final OutputStream outputStream;

        public InputStreamDrainer(InputStream inputStream, OutputStream outputStream) {
            this.inputStream = inputStream;
            this.outputStream = outputStream;
        }

        @Override
        public void run() {
            try (BufferedInputStream bis = new BufferedInputStream(inputStream);
                 BufferedOutputStream bos = new BufferedOutputStream(outputStream)) {
                byte[] buffer = new byte[1024];
                int bytesRead;
                while ((bytesRead = bis.read(buffer)) != -1) {
                    bos.write(buffer, 0, bytesRead);
                }
            } catch (IOException e) {
                throw new RuntimeException("Error draining input stream", e);
            }
        }
    }
}

