import java.io.*;
import java.nio.charset.Charset;
import java.nio.file.Files;
import java.time.LocalDateTime;
import java.util.concurrent.*;

public class Command {
    private Process process;
    private OutputStream stdIn;
    private File stdOut;
    private File stdErr;
    private String nextLine;
    private String exitCommand;
    private Charset charset;
    private LocalDateTime startTime;

    private Command(String init, String nextLine, String exit, Charset charset) throws IOException {
        this.nextLine = nextLine;
        this.exitCommand = exit;
        this.charset = charset;
        startProcess(init);
    }

    public static Command of(String init) throws IOException {
        return of(init, Charset.defaultCharset());
    }

    public static Command of(String init, Charset charset) throws IOException {
        return of(init, System.lineSeparator(), "exit", charset);
    }

    public static Command of(String init, String nextLine, String exit, Charset charset) throws IOException {
        return new Command(init, nextLine, exit, charset);
    }

    private void startProcess(String init) throws IOException {
        ProcessBuilder processBuilder = new ProcessBuilder(init.split(" "));
        stdOut = Files.createTempFile("stdout", ".txt").toFile();
        stdErr = Files.createTempFile("stderr", ".txt").toFile();
        processBuilder.redirectOutput(ProcessBuilder.Redirect.to(stdOut));
        processBuilder.redirectError(ProcessBuilder.Redirect.to(stdErr));
        process = processBuilder.start();
        stdIn = process.getOutputStream();
        startTime = LocalDateTime.now();
    }

    public Command write(String str) throws IOException {
        stdIn.write(str.getBytes(charset));
        stdIn.flush();
        return this;
    }

    public Command line() throws IOException {
        write(nextLine);
        return this;
    }

    public Command exit() throws IOException {
        write(exitCommand);
        line();
        return this;
    }

    public Command exec(String str) throws IOException {
        write(str);
        line();
        return this;
    }

    public CommandResult result() throws IOException, InterruptedException {
        return result(0);
    }

    public CommandResult result(long millis) throws IOException, InterruptedException, TimeoutException {
        boolean finished;
        if (millis > 0) {
            finished = process.waitFor(millis, TimeUnit.MILLISECONDS);
        } else {
            process.waitFor();
            finished = true;
        }
        if (!finished) {
            throw new TimeoutException("Process timeout");
        }
        return new CommandResult(readFileContent(stdOut), readFileContent(stdErr), process.exitValue());
    }

    private String readFileContent(File file) throws IOException {
        return new String(Files.readAllBytes(file.toPath()), charset);
    }

    public void close() {
        process.destroy();
        try {
            stdIn.close();
            Files.deleteIfExists(stdOut.toPath());
            Files.deleteIfExists(stdErr.toPath());
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static class CommandResult {
        private final String stdout;
        private final String stderr;
        private final int exitCode;

        public CommandResult(String stdout, String stderr, int exitCode) {
            this.stdout = stdout;
            this.stderr = stderr;
            this.exitCode = exitCode;
        }

        public String getStdout() {
            return stdout;
        }

        public String getStderr() {
            return stderr;
        }

        public int getExitCode() {
            return exitCode;
        }
    }
