import java.io.IOException;
import java.io.OutputStream;
import java.nio.channels.FileChannel;
import java.nio.file.OpenOption;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import java.nio.ByteBuffer;
import java.nio.file.Files;

public class FileChannelUtils {

    public static OutputStream newOutputStream(FileChannel channel, boolean forceAtClose) {
        return new OutputStream() {
            private final FileChannel fileChannel = channel;
            private final boolean force = forceAtClose;

            @Override
            public void write(int b) throws IOException {
                ByteBuffer buffer = ByteBuffer.allocate(1);
                buffer.put((byte) b);
                buffer.flip();
                while (buffer.hasRemaining()) {
                    fileChannel.write(buffer);
                }
            }

            @Override
            public void write(byte[] b, int off, int len) throws IOException {
                ByteBuffer buffer = ByteBuffer.wrap(b, off, len);
                while (buffer.hasRemaining()) {
                    fileChannel.write(buffer);
                }
            }

            @Override
            public void close() throws IOException {
                try {
                    if (force) {
                        fileChannel.force(true);
                    }
                } finally {
                    fileChannel.close();
                }
            }
        };
    }

    public static OutputStream newOutputStreamForceAtClose(Path p, OpenOption... options) throws IOException {
        FileChannel channel = newFileChannel(p, options);
        return newOutputStream(channel, true);
    }

    public static FileChannel newFileChannel(Path p, OpenOption... options) throws IOException {
        return FileChannel.open(p, options);
    }

    public static void main(String[] args) {
        // Example usage
        Path path = Path.of("example.txt");
        try (OutputStream os = newOutputStreamForceAtClose(path, StandardOpenOption.CREATE, StandardOpenOption.WRITE)) {
            os.write("Hello, World!".getBytes());
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
}

