            String line;
            while ((line = stdOutput.readLine()) != null) {
                logger.info(line);
            }
            while ((line = stdError.readLine()) != null) {
                logger.severe(line);
            }
        }

        int exitCode = process.waitFor();
        if (exitCode != 0) {
            throw new IOException("Diff tool exited with code " + exitCode);
        }
    }

    private static String[] buildCommand(String mCustomDiffPath, String mCustomDiffPathArgs, File oldFile, File newFile, File diffFile) {
        String[] args = mCustomDiffPathArgs.split(" ");
        String[] command = new String[args.length + 4];
        command[0] = mCustomDiffPath;
        System.arraycopy(args, 0, command, 1, args.length);
        command[args.length + 1] = oldFile.getAbsolutePath();
        command[args.length + 2] = newFile.getAbsolutePath();
        command[args.length + 3] = diffFile.getAbsolutePath();
        return command;
    }
}

