            while (buffer.hasRemaining()) {
                String daemonName = readString();
                if (daemonName == null || daemonName.isEmpty()) break;
                DaemonInfo info = new DaemonInfo(daemonName);
                infosMap.put(daemonName, info);
            }
        }
    }

    public void doUpdate(Runnable updater) {
        synchronized (lck) {
            try (FileLock lock = tryLock()) {
                updater.run();
                buffer.position(0);
                for (DaemonInfo info : infosMap.values()) {
                    writeString(info.getName());
                }
            } catch (IOException e) {
                LOGGER.severe("Failed to update registry: " + e.getMessage());
                reset();
            }
        }
    }

    private FileLock tryLock() throws IOException {
        long startTime = System.currentTimeMillis();
        while (true) {
            FileLock lock = channel.tryLock();
            if (lock != null) {
                return lock;
            }
            if (System.currentTimeMillis() - startTime > LOCK_TIMEOUT_MS) {
                throw new IOException("Timeout while trying to acquire lock");
            }
            try {
                Thread.sleep(100);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                throw new IOException("Interrupted while waiting for lock", e);
            }
        }
    }

    private void reset() {
        synchronized (lck) {
            infosMap.clear();
            buffer.position(0);
        }
    }

    private String readString() {
        int length = buffer.getInt();
        if (length <= 0 || length > buffer.remaining()) {
            return null;
        }
        byte[] bytes = new byte[length];
        buffer.get(bytes);
        return new String(bytes, StandardCharsets.UTF_8);
    }

    private void writeString(String str) {
        byte[] bytes = str.getBytes(StandardCharsets.UTF_8);
        buffer.putInt(bytes.length);
        buffer.put(bytes);
    }

    private long nextPowerOf2(long a, long min) {
        long result = Math.max(a, min);
        result--;
        result |= result >> 1;
        result |= result >> 2;
        result |= result >> 4;
        result |= result >> 8;
        result |= result >> 16;
        result |= result >> 32;
        return result + 1;
    }
}

class DaemonInfo {
    private final String name;

    public DaemonInfo(String name) {
        this.name = name;
    }

    public String getName() {
        return name;
    }
}

