        }
        if (mUseRoot) {
            List<String> rootCommand = new ArrayList<>();
            rootCommand.add("su");
            rootCommand.add("-c");
            rootCommand.add(String.join(" ", command));
            pb.command(rootCommand);
        }
        return pb.start();
    }

    public Thread log(InputStream is, int priority) {
