        log(process.getErrorStream(), Log.ERROR);
        StringBuilder output = new StringBuilder();
        if (returnStdOut) {
            try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
                String line;
                while ((line = reader.readLine()) != null) {
                    output.append(line).append("\n");
                }
            }
        }
        process.waitFor();
        wakeLock.release();
        return output.toString();
    }

    public List<String> getProcessPIDs(String processName) throws Exception {
        List<String> pids = new ArrayList<>();
        String command = mUseRoot ? "su -c ps" : "ps";
        Process process = setupAndLaunch(null, new String[]{command});
        try (BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()))) {
            String line;
            while ((line = reader.readLine()) != null) {
                if (line.contains(processName)) {
                    String[] parts = line.split("\\s+");
                    if (parts.length > 1) {
                        pids.add(parts[1]);
                    }
                }
            }
        }
        process.waitFor();
        return pids;
    }
}

