        processBuilder.directory(workingDir);
        processBuilder.redirectInput(inputRedirect);
        Process process = processBuilder.start();
        this.startTime = System.currentTimeMillis();

        if (logExecution) {
            errLog.log("Executing command: " + execCommandStr);
        }

        ExecutorService executor = Executors.newFixedThreadPool(2);
        executor.submit(() -> outReceiver.receive(process.getInputStream()));
        executor.submit(() -> errReceiver.receive(process.getErrorStream()));
        executor.shutdown();

