import redis.clients.jedis.Connection;
import redis.clients.jedis.CommandObject;
import redis.clients.jedis.CommandObjects;
import redis.clients.jedis.Response;
import redis.clients.jedis.exceptions.JedisException;

import java.util.ArrayList;
import java.util.List;
import java.util.Queue;
import java.util.concurrent.LinkedBlockingQueue;

public class ReliableTransaction {
    private final Queue<Response<?>> pipelinedResponses;
    private final Connection connection;
    private final boolean closeConnection;
    private boolean broken;
    private boolean inWatch;
    private boolean inMulti;
    private final CommandObjects commandObjects;

    public ReliableTransaction(Connection connection, boolean closeConnection, boolean broken, CommandObjects commandObjects) {
        this.connection = connection;
        this.closeConnection = closeConnection;
        this.broken = broken;
        this.commandObjects = commandObjects;
        this.pipelinedResponses = new LinkedBlockingQueue<>();
    }

    public void multi() {
        if (inMulti) {
            throw new JedisException("MULTI already started");
        }
        connection.sendCommand(commandObjects.multi());
        inMulti = true;
    }

    public String watch(String... keys) {
        if (inMulti) {
            throw new JedisException("Cannot WATCH after MULTI");
        }
        connection.sendCommand(commandObjects.watch(keys));
        inWatch = true;
        return connection.getStatusCodeReply();
    }

    public String unwatch() {
        if (!inWatch) {
            throw new JedisException("No keys are being watched");
        }
        connection.sendCommand(commandObjects.unwatch());
        inWatch = false;
        return connection.getStatusCodeReply();
    }

    public <T> Response<T> appendCommand(CommandObject<T> commandObject) {
        if (!inMulti) {
            throw new JedisException("MULTI not started");
        }
        connection.sendCommand(commandObject);
        Response<T> response = new Response<>(commandObject.getBuilder());
        pipelinedResponses.add(response);
        return response;
    }

    public void close() {
        try {
            if (broken) {
                connection.close();
            } else if (closeConnection) {
                connection.close();
            }
        } catch (Exception e) {
            throw new JedisException("Error closing connection", e);
        }
    }

    public void clear() {
        if (inMulti) {
            discard();
        }
        if (inWatch) {
            unwatch();
        }
        pipelinedResponses.clear();
    }

    public List<Object> exec() {
        if (!inMulti) {
            throw new JedisException("MULTI not started");
        }
        connection.sendCommand(commandObjects.exec());
        List<Object> results = connection.getObjectMultiBulkReply();
        inMulti = false;
        for (Response<?> response : pipelinedResponses) {
            response.set(results.remove(0));
        }
        pipelinedResponses.clear();
        return results;
    }

    public String discard() {
        if (!inMulti) {
            throw new JedisException("MULTI not started");
        }
        connection.sendCommand(commandObjects.discard());
        inMulti = false;
        pipelinedResponses.clear();
        return connection.getStatusCodeReply();
    }
}

