public class RedisSentinelConnector {

    private final ClientResources clientResources;

    public RedisSentinelConnector(ClientResources clientResources) {
        this.clientResources = clientResources;
    }

    public StatefulRedisSentinelConnection<String, String> connectToSentinelDirectly(String host) {
        RedisURI redisURI = RedisURI.Builder.sentinel(host).withSsl(true).withVerifyPeer(false).build();
        RedisClient redisClient = RedisClient.create(clientResources, redisURI);
        return redisClient.connectSentinel();
    }

    public StatefulRedisConnection<String, String> connectToMasterViaSentinel(String host, String masterId, File truststore) throws Exception {
        SSLContext sslContext = createSSLContext(truststore);
        RedisURI redisURI = RedisURI.Builder.sentinel(host, masterId)
                .withSsl(true)
                .withSslContext(sslContext)
                .build();
        RedisClient redisClient = RedisClient.create(clientResources, redisURI);
        StatefulRedisSentinelConnection<String, String> sentinelConnection = redisClient.connectSentinel();
        RedisSentinelCommands<String, String> commands = sentinelConnection.sync();
        RedisURI masterUri = RedisURI.create(commands.getMasterAddrByName(masterId));
        sentinelConnection.close();
        return redisClient.connect(masterUri);
    }

    private SSLContext createSSLContext(File truststore) throws Exception {
        CertificateFactory cf = CertificateFactory.getInstance("X.509");
        FileInputStream fis = new FileInputStream(truststore);
        X509Certificate caCert = (X509Certificate) cf.generateCertificate(fis);
        KeyStore ks = KeyStore.getInstance(KeyStore.getDefaultType());
        ks.load(null, null);
        ks.setCertificateEntry("caCert", caCert);
        SSLContext sslContext = SSLContext.getInstance("TLS");
        sslContext.init(null, null, null);
        return sslContext;
    }

    public void closeConnection(AutoCloseable connection, RedisClient client) {
        try {
            if (connection != null) {
                connection.close();
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        if (client != null) {
            client.shutdown();
        }
    }
