import redis.clients.jedis.JedisPoolConfig;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.TimeUnit;

public class RedisPooledClient {
    private final JedisPooled jedisPool;

    public RedisPooledClient(String host, int port, String password) {
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        poolConfig.setMaxTotal(128);
        poolConfig.setMaxIdle(128);
        poolConfig.setMinIdle(16);
        poolConfig.setTestOnBorrow(true);
        poolConfig.setTestOnReturn(true);
        poolConfig.setTestWhileIdle(true);
        poolConfig.setMinEvictableIdleTimeMillis(Duration.ofSeconds(60).toMillis());
        poolConfig.setTimeBetweenEvictionRunsMillis(Duration.ofSeconds(30).toMillis());
        poolConfig.setNumTestsPerEvictionRun(3);
        poolConfig.setBlockWhenExhausted(true);

        this.jedisPool = new JedisPooled(host, port, poolConfig, password);
    }

    public void performOperations(int totalOperations) {
        ExecutorService executor = Executors.newFixedThreadPool(10);
        for (int i = 0; i < totalOperations; i++) {
            final int operationId = i;
            executor.submit(() -> {
                String key = "key" + operationId;
                String value = "value" + operationId;
                jedisPool.set(key, value);
                String retrievedValue = jedisPool.get(key);
                System.out.println("Operation " + operationId + ": Set " + key + " = " + value + ", Retrieved = " + retrievedValue);
            });
        }
        executor.shutdown();
        try {
            executor.awaitTermination(1, TimeUnit.HOURS);
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
        }
    }

    public void close() {
        jedisPool.close();
