import io.lettuce.core.api.StatefulRedisConnection;
import io.lettuce.core.resource.ClientResources;
import io.lettuce.core.resource.DefaultClientResources;
import io.netty.handler.ssl.SslContextBuilder;
import io.netty.handler.ssl.util.InsecureTrustManagerFactory;

import javax.net.ssl.SSLException;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.security.KeyStore;
import java.security.NoSuchAlgorithmException;
import java.security.cert.CertificateException;
import java.security.cert.X509Certificate;
import java.util.concurrent.TimeUnit;

public class RedisSSLConnection {

    private RedisClient redisClient;
    private StatefulRedisConnection<String, String> connection;

    public RedisSSLConnection(String redisUri, String hostName, String truststorePath, String truststorePassword) throws Exception {
        ClientResources clientResources = DefaultClientResources.builder()
                .sslContextBuilderCustomizer(sslContextBuilder -> configureSsl(sslContextBuilder, truststorePath, truststorePassword))
                .build();

        RedisURI uri = RedisURI.create(redisUri);
        uri.setVerifyPeer(false);
        uri.setSsl(true);
        uri.setHostname(hostName);

        redisClient = RedisClient.create(clientResources, uri);
        connection = redisClient.connect();
    }

    private void configureSsl(SslContextBuilder sslContextBuilder, String truststorePath, String truststorePassword) throws Exception {
        KeyStore trustStore = KeyStore.getInstance(KeyStore.getDefaultType());
        try (FileInputStream trustStoreFile = new FileInputStream(new File(truststorePath))) {
            trustStore.load(trustStoreFile, truststorePassword.toCharArray());
        }

        sslContextBuilder.trustManager((X509Certificate[] chain, String authType) -> {
            for (X509Certificate cert : chain) {
                cert.checkValidity();
            }
        }).trustManager(InsecureTrustManagerFactory.INSTANCE);
    }

    public StatefulRedisConnection<String, String> getConnection() {
        return connection;
    }

    public void close() {
        if (connection != null) {
            connection.close();
        }
        if (redisClient != null) {
            redisClient.shutdown(0, 0, TimeUnit.MILLISECONDS);
        }
    }
}

