            logger.info("Set string value for key: {}", key);
            return result;
        }
    }

    public static String setObjectValue(String key, Object obj, int seconds) {
        try (ShardedJedis jedis = getInstance()) {
            byte[] serializedObject = serialize(obj);
            String result = jedis.setex(key.getBytes(), seconds, serializedObject);
            logger.info("Set object value for key: {}", key);
            return result;
        }
    }

