public class RedisJsonClient {
    private final RedisClient client;
    private final StatefulRedisConnection<String, String> connection;
    private final RedisCommands<String, String> syncCommands;

    public RedisJsonClient(String host, int port) {
        this.client = RedisClient.create("redis://" + host + ":" + port);
        this.connection = client.connect();
        this.syncCommands = connection.sync();
    }

    public JsonValue jsonGet(String key, JsonPath path) {
        String result = syncCommands.dispatch(CommandType.JSON.GET, new CommandArgs<>(CommandKeyword.KEY, key).add(path.toString()));
        return Json.createReader(new java.io.StringReader(result)).readValue();
    }

    public String jsonSet(String key, JsonPath path, JsonValue value) {
        return syncCommands.dispatch(CommandType.JSON.SET, new CommandArgs<>(CommandKeyword.KEY, key).add(path.toString()).add(value.toString()));
