class SeekableInMemoryByteChannel implements SeekableByteChannel {
    private ByteBuffer buffer;
    private boolean open = true;

    public SeekableInMemoryByteChannel(int capacity) {
        this.buffer = ByteBuffer.allocate(capacity);
    }

    @Override
    public int read(ByteBuffer dst) throws IOException {
        int remaining = buffer.remaining();
        if (remaining == 0) return -1;
        int toRead = Math.min(dst.remaining(), remaining);
        for (int i = 0; i < toRead; i++) {
            dst.put(buffer.get());
        }
        return toRead;
    }

    @Override
    public int write(ByteBuffer src) throws IOException {
        int toWrite = Math.min(src.remaining(), buffer.remaining());
        for (int i = 0; i < toWrite; i++) {
            buffer.put(src.get());
        }
        return toWrite;
    }

    @Override
    public long position() throws IOException {
        return buffer.position();
    }

    @Override
    public SeekableByteChannel position(long newPosition) throws IOException {
        buffer.position((int) newPosition);
        return this;
    }

    @Override
    public long size() throws IOException {
        return buffer.capacity();
