import redis.clients.jedis.Connection;
import redis.clients.jedis.ConnectionPool;
import redis.clients.jedis.HostAndPort;
import redis.clients.jedis.JedisClientConfig;
import redis.clients.jedis.JedisSentinelPool;
import redis.clients.jedis.exceptions.JedisException;

import java.util.Set;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.concurrent.atomic.AtomicBoolean;

public class SentineledConnectionProvider {
    private HostAndPort currentMaster;
    private ConnectionPool pool;
    private final String masterName;
    private final JedisClientConfig masterClientConfig;
    private final JedisClientConfig sentinelClientConfig;
    private final ExecutorService sentinelListenerExecutor;
    private final AtomicBoolean running = new AtomicBoolean(false);

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig, Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.masterClientConfig = masterClientConfig;
        this.sentinelClientConfig = sentinelClientConfig;
        this.sentinelListenerExecutor = Executors.newSingleThreadExecutor();
        HostAndPort master = initSentinels(sentinels);
        initMaster(master);
        startSentinelListener(sentinels);
    }

    public Connection getConnection() {
        return pool.getResource();
    }

    private void initMaster(HostAndPort master) {
        if (pool != null) {
            pool.close();
        }
        this.currentMaster = master;
        this.pool = new ConnectionPool(masterClientConfig, currentMaster);
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        try (JedisSentinelPool sentinelPool = new JedisSentinelPool(masterName, sentinels, sentinelClientConfig)) {
            return sentinelPool.getCurrentHostMaster();
        } catch (JedisException e) {
            throw new RuntimeException("Failed to initialize Sentinels", e);
        }
    }

    private void startSentinelListener(Set<HostAndPort> sentinels) {
        running.set(true);
        sentinelListenerExecutor.submit(() -> {
            while (running.get()) {
                try {
                    HostAndPort newMaster = initSentinels(sentinels);
                    if (!newMaster.equals(currentMaster)) {
                        initMaster(newMaster);
                    }
                    Thread.sleep(5000);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                } catch (Exception e) {
                    // Log exception
                }
            }
        });
    }

    public void shutdown() {
        running.set(false);
        sentinelListenerExecutor.shutdown();
        if (pool != null) {
            pool.close();
        }
    }

    private class SentinelListener implements Runnable {
        private final HostAndPort node;

        public SentinelListener(HostAndPort node) {
            this.node = node;
        }

        @Override
        public void run() {
            while (running.get()) {
                try {
                    HostAndPort newMaster = initSentinels(Set.of(node));
                    if (!newMaster.equals(currentMaster)) {
                        initMaster(newMaster);
                    }
                    Thread.sleep(5000);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                } catch (Exception e) {
                    // Log exception
                }
            }
        }
    }
}

