import org.springframework.stereotype.Component;

import javax.annotation.PostConstruct;
import java.util.List;

@Component
public class PlanConsumer {

    @Value("${rocketmq.consumer.group}")
    private String consumerGroup;

    @Value("${rocketmq.namesrv.addr}")
    private String namesrvAddr;

    private static final Logger logger = LoggerFactory.getLogger(PlanConsumer.class);

    @PostConstruct
    public void defaultMQPushConsumer() {
        DefaultMQPushConsumer consumer = new DefaultMQPushConsumer(consumerGroup);
        consumer.setNamesrvAddr(namesrvAddr);
        try {
            consumer.subscribe("YourTopicName", "*");
            consumer.registerMessageListener(new MessageListenerConcurrently() {
                @Override
                public ConsumeConcurrentlyStatus consumeMessage(List<MessageExt> msgs, ConsumeConcurrentlyContext context) {
                    for (MessageExt msg : msgs) {
                        boolean success = processMessage(msg);
                        if (!success) {
                            int retryCount = msg.getReconsumeTimes();
                            if (retryCount >= 3) {
                                logger.error("Message consumption failed after 3 retries: {}", new String(msg.getBody()));
                                return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
                            }
                            return ConsumeConcurrentlyStatus.RECONSUME_LATER;
                        }
                    }
                    return ConsumeConcurrentlyStatus.CONSUME_SUCCESS;
                }
            });
            consumer.start();
            logger.info("RocketMQ Consumer started successfully.");
        } catch (MQClientException e) {
            logger.error("Failed to start RocketMQ Consumer", e);
        }
    }

    private boolean processMessage(MessageExt msg) {
        try {
            String messageBody = new String(msg.getBody());
            logger.info("Received message: {}", messageBody);
            // Add your message processing logic here
            return true;
        } catch (Exception e) {
            logger.error("Error processing message: {}", new String(msg.getBody()), e);
            return false;
        }
    }
}

