import org.ietf.jgss.*;

import java.io.*;
import java.net.Socket;

public class GSSClient {
    private Socket s;

    public static void main(String[] args) {
        if (args.length != 3) {
            usage();
            return;
        }

        String serverName = args[0];
        String host = args[1];
        int port = Integer.parseInt(args[2]);

        try {
            GSSClient client = new GSSClient();
            client.s = new Socket(host, port);
            Oid mechOid = new Oid("1.3.6.1.5.5.2"); // Kerberos V5 OID
            GSSContext context = client.createCtxt(serverName, mechOid);

            client.sendMsgToPeer(context, "Hello, Server!");
            client.verifyMsgFromPeer(context, "Hello, Server!");

            client.exit(0);
        } catch (Exception e) {
            print("Error: " + e.getMessage());
            System.exit(1);
        }
    }

    private GSSContext createCtxt(String serverName, Oid mechOid) throws GSSException {
        GSSManager manager = GSSManager.getInstance();
        GSSName serverGSSName = manager.createName(serverName, GSSName.NT_HOSTBASED_SERVICE);
        GSSContext context = manager.createContext(serverGSSName, mechOid, null, GSSContext.DEFAULT_LIFETIME);
        context.requestMutualAuth(true);
        context.requestConf(true);
        context.requestInteg(true);

        InputStream in = s.getInputStream();
        OutputStream out = s.getOutputStream();

        byte[] token = new byte[0];
        while (!context.isEstablished()) {
            token = context.initSecContext(token, 0, token.length);
            if (token != null) {
                out.write(token);
                out.flush();
            }
            if (!context.isEstablished()) {
                token = new byte[in.read()];
                in.read(token);
            }
        }
        return context;
    }

    private void sendMsgToPeer(GSSContext aCtxt, String msg) throws GSSException, IOException {
        byte[] messageBytes = msg.getBytes("UTF-8");
        MessageProp prop = new MessageProp(0, true);
        byte[] encryptedMsg = aCtxt.wrap(messageBytes, 0, messageBytes.length, prop);

        OutputStream out = s.getOutputStream();
        out.write(encryptedMsg);
        out.flush();
    }

    private void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws GSSException, IOException {
        InputStream in = s.getInputStream();
        byte[] receivedMsg = new byte[in.read()];
        in.read(receivedMsg);

        MessageProp prop = new MessageProp(0, false);
        byte[] decryptedMsg = aCtxt.unwrap(receivedMsg, 0, receivedMsg.length, prop);

        String receivedString = new String(decryptedMsg, "UTF-8");
        if (!receivedString.equals(msg)) {
            throw new GSSException(GSSException.BAD_MIC);
        }
    }

    private static void usage() {
        print("Usage: java GSSClient <serverName> <host> <port>");
    }

    private static void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) {
        try {
            if (s != null) {
                s.close();
            }
        } catch (IOException e) {
            print("Error closing socket: " + e.getMessage());
        }
        System.exit(status);
    }
}

