import javax.jms.Connection;
import javax.jms.ConnectionFactory;
import javax.jms.Destination;
import javax.jms.JMSException;
import javax.jms.MessageProducer;
import javax.jms.Session;
import javax.jms.TextMessage;
import org.apache.activemq.ActiveMQConnection;
import org.apache.activemq.ActiveMQConnectionFactory;

public class JmsProducer {
    private static final String USERNAME = ActiveMQConnection.DEFAULT_USER;
    private static final String PASSWORD = ActiveMQConnection.DEFAULT_PASSWORD;
    private static final String BROKERURL = ActiveMQConnection.DEFAULT_BROKER_URL;
    private static final int SENDNUM = 10;

    public void run() {
        ConnectionFactory connectionFactory = new ActiveMQConnectionFactory(USERNAME, PASSWORD, BROKERURL);
        Connection connection = null;
        Session session = null;
        try {
            connection = connectionFactory.createConnection();
            connection.start();
            session = connection.createSession(false, Session.AUTO_ACKNOWLEDGE);

            Destination queue = session.createQueue("TestQueue");
            Destination topic = session.createTopic("TestTopic");

            MessageProducer queueProducer = session.createProducer(queue);
            MessageProducer topicProducer = session.createProducer(topic);

            sendMessage(session, queueProducer, "QUEUE");
            sendMessage(session, topicProducer, "TOPIC");
        } catch (JMSException e) {
            e.printStackTrace();
        } finally {
            try {
                if (session != null) session.close();
                if (connection != null) connection.close();
            } catch (JMSException e) {
                e.printStackTrace();
            }
        }
    }

    private void sendMessage(Session session, MessageProducer messageProducer, String type) throws JMSException {
        for (int i = 0; i < SENDNUM; i++) {
            String text = "Message " + i + " from " + type + " at " + System.currentTimeMillis();
            TextMessage message = session.createTextMessage(text);
            messageProducer.send(message);
            System.out.println("Sent: " + text);
        }
    }

    public static void main(String[] args) {
        new JmsProducer().run();
    }
}

