    protected String doInBackground(Void... params) {
        Socket socket = null;
        try {
            socket = new Socket(HOST, PORT);
            OutputStream outputStream = socket.getOutputStream();
            outputStream.write(HTTP_GET_COMMAND.getBytes());
            outputStream.flush();

            InputStream inputStream = socket.getInputStream();
            return readStream(inputStream);
        } catch (Exception e) {
            e.printStackTrace();
            return null;
        } finally {
            if (socket != null) {
                try {
                    socket.close();
                } catch (Exception e) {
                    e.printStackTrace();
                }
            }
        }
