import org.slf4j.LoggerFactory;

import java.io.IOException;
import java.io.InputStream;
import java.net.Socket;
import java.util.concurrent.BlockingQueue;

public class MiniCapInputSocketThread extends Thread {
    private static final Logger log = LoggerFactory.getLogger(MiniCapInputSocketThread.class);
    private final IDevice iDevice;
    private final BlockingQueue<byte[]> dataQueue;
    private final MiniCapLocalThread miniCapPro;
    private final WebSocket session;
    private Socket socket;
    private InputStream inputStream;

    public MiniCapInputSocketThread(IDevice iDevice, BlockingQueue<byte[]> dataQueue, MiniCapLocalThread miniCapPro, WebSocket session) {
        this.iDevice = iDevice;
        this.dataQueue = dataQueue;
        this.miniCapPro = miniCapPro;
        this.session = session;
    }

    @Override
    public void run() {
        try {
            connectToMinicap();
            readDataFromMinicap();
        } catch (IOException e) {
            log.error("Error in MiniCapInputSocketThread: ", e);
        } finally {
            cleanupResources();
        }
    }

    private void connectToMinicap() throws IOException {
        int localPort = miniCapPro.getLocalPort();
        socket = new Socket("localhost", localPort);
        inputStream = socket.getInputStream();
        log.info("Connected to minicap on port: " + localPort);
    }

    private void readDataFromMinicap() throws IOException {
        byte[] buffer = new byte[4096];
        int bytesRead;
        while ((bytesRead = inputStream.read(buffer)) != -1) {
            byte[] data = new byte[bytesRead];
            System.arraycopy(buffer, 0, data, 0, bytesRead);
            dataQueue.put(data);
            if (session != null && session.isOpen()) {
                session.send(data);
            }
        }
    }

    private void cleanupResources() {
        try {
            if (inputStream != null) {
                inputStream.close();
            }
            if (socket != null) {
                socket.close();
            }
        } catch (IOException e) {
            log.error("Error closing resources: ", e);
        }
        log.info("MiniCapInputSocketThread resources cleaned up.");
    }
}

