    private void parseHeader() throws IOException {
        String header = "FileName:" + mFileInfo.getFileName() + ";FileSize:" + mFileInfo.getFileSize() + "\n";
        mOutputStream.write(header.getBytes());
        mOutputStream.flush();
    }

    private void parseBody() throws IOException {
        FileInputStream fileInputStream = new FileInputStream(mFileInfo.getFile());
        byte[] buffer = new byte[4096];
        long totalBytesRead = 0;
        int bytesRead;
        while ((bytesRead = fileInputStream.read(buffer)) != -1) {
            mOutputStream.write(buffer, 0, bytesRead);
            totalBytesRead += bytesRead;
            if (mOnSendListener != null) {
                mOnSendListener.onProgress(totalBytesRead, mFileInfo.getFileSize());
            }
        }
        mOutputStream.flush();
        fileInputStream.close();
    }

    private void finish() {
        try {
            if (mOutputStream != null) {
                mOutputStream.close();
            }
            if (mSocket != null) {
                mSocket.close();
            }
        } catch (IOException e) {
            // Handle exception during cleanup
        }
    }

    public interface OnSendListener {
        void onStart();
        void onProgress(long bytesSent, long totalBytes);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable throwable, FileInfo fileInfo);
    }
}

