    }

    public void setupHttpClient(ThreadPoolExecutor threadPool) {
        this.httpClient = HttpClient.newBuilder()
                .version(Version.HTTP_2)
                .followRedirects(Redirect.NORMAL)
                .executor(threadPool)
                .build();
    }

    public void closeClient() {
        if (httpClient != null) {
            ExecutorService executor = (ExecutorService) httpClient.executor().orElse(null);
            if (executor != null) {
                executor.shutdown();
                try {
                    if (!executor.awaitTermination(60, TimeUnit.SECONDS)) {
                        executor.shutdownNow();
                    }
                } catch (InterruptedException e) {
                    executor.shutdownNow();
                }
            }
        }
    }

    private HttpResponse<String> executeAndDefaultAssertions(HttpRequest request) throws Exception {
        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        if (response.statusCode() >= 300 && response.statusCode() < 400) {
            throw new Exception("Redirects not handled automatically");
        }
        return response;
    }

    public HttpResponse<String> get(String path, String[]... headers) throws Exception {
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
                .uri(new URI("http", null, address.getHostName(), address.getPort(), path, null, null))
                .GET();
        for (String[] header : headers) {
            requestBuilder.header(header[0], header[1]);
        }
        HttpRequest request = requestBuilder.build();
        return executeAndDefaultAssertions(request);
    }

    public HttpResponse<String> browserGet(String uri) throws Exception {
