        SocketChannel socketChannel = SocketChannel.open();
        socketChannel.configureBlocking(false);
        socketChannel.connect(address);
        String connectionId = generateConnectionId(socketChannel);
        SelectionKey key = socketChannel.register(nioSelector, SelectionKey.OP_CONNECT);
        keyMap.put(connectionId, key);
        key.attach(new ConnectionContext(readBufferSize, writeBufferSize, portType));
        return connectionId;
    }

    private String generateConnectionId(SocketChannel socketChannel) throws IOException {
        InetSocketAddress localAddress = (InetSocketAddress) socketChannel.getLocalAddress();
        InetSocketAddress remoteAddress = (InetSocketAddress) socketChannel.getRemoteAddress();
        return localAddress.toString() + "-" + remoteAddress.toString();
    }

    private Transmission createTransmission(String connectionId, SelectionKey key, String host, int port, PortType portType, SSLFactory.Mode mode) throws IOException {
        ConnectionContext context = (ConnectionContext) key.attachment();
