import java.util.concurrent.atomic.AtomicLong;

public class NIOAcceptor implements Runnable {
    private final int port;
    private final Selector selector;
    private final ServerSocketChannel serverChannel;
    private final FrontendConnectionFactory factory;
    private final NIOProcessor[] processors;
    private int nextProcessor;
    private long acceptCount;

    public NIOAcceptor(int port, FrontendConnectionFactory factory, NIOProcessor[] processors) throws IOException {
        this.port = port;
        this.factory = factory;
        this.processors = processors;
        this.selector = Selector.open();
        this.serverChannel = ServerSocketChannel.open();
        this.serverChannel.configureBlocking(false);
        this.serverChannel.bind(new InetSocketAddress(port));
        this.serverChannel.register(selector, SelectionKey.OP_ACCEPT);
        this.nextProcessor = 0;
        this.acceptCount = 0;
    }

    @Override
    public void run() {
        while (true) {
            try {
                selector.select();
                for (SelectionKey key : selector.selectedKeys()) {
                    if (key.isAcceptable()) {
                        accept();
                    }
                }
                selector.selectedKeys().clear();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void accept() {
        try {
            SocketChannel clientChannel = serverChannel.accept();
            if (clientChannel != null) {
                clientChannel.configureBlocking(false);
                FrontendConnection connection = factory.createConnection(clientChannel);
                NIOProcessor processor = nextProcessor();
                processor.addConnection(connection);
                acceptCount++;
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private NIOProcessor nextProcessor() {
        NIOProcessor processor = processors[nextProcessor];
        nextProcessor = (nextProcessor + 1) % processors.length;
        return processor;
    }

    private void closeChannel(SocketChannel channel) {
        try {
            if (channel != null) {
                channel.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public static class AcceptIdGenerator {
