            while(running) {
                for(int i = 1;i<buffers.length;i++) {
                    buffers[i] = toSend.take();
                }

                channel.write(buffers);
                buffers[0].rewind();
            }
        } catch (IOException | InterruptedException e) {
            running = false;
            e.printStackTrace();
        }
    }

    public void stop() {
        running = false;
        try {
            channel.close();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
