                ByteBuffer buffer = ByteBuffer.allocate(HEADER_LENGTH + PEER_LENGTH * PEERS_PER_PACKET);
                buffer.put(id);
                int count = 0;
                while (count < PEERS_PER_PACKET && !toSend.isEmpty()) {
                    ByteBuffer peerBuffer = toSend.poll();
                    if (peerBuffer != null) {
                        buffer.put(peerBuffer);
                        count++;
                    }
                }
                if (count > 0) {
                    buffer.flip();
                    channel.send(buffer, new InetSocketAddress("224.0.0.1", 6969));
                }
                Thread.sleep(100);
            }
        } catch (IOException | InterruptedException e) {
            e.printStackTrace();
        }
    }

    public void stop() {
        running = false;
        try {
            if (channel != null) {
                channel.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }
