            DescribeConfigsResult result = _adminClient.describeConfigs(resources);
            Map<ConfigResource, Config> configs = result.all().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT);
            return configs.entrySet().stream()
                    .collect(Collectors.toMap(
                            entry -> entry.getKey().name(),
                            entry -> convertConfigToProperties(entry.getValue())
                    ));
        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve configurations for topics: {}", topics, e);
            return Collections.emptyMap();
        }
    }

    public Map<String, Properties> allTopicConfigs() {
        try {
            ListTopicsResult listTopicsResult = _adminClient.listTopics();
            Set<String> topicNames = listTopicsResult.names().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT);
            return topicConfigs(topicNames);
        } catch (InterruptedException | ExecutionException e) {
            LOG.warn("Failed to retrieve all topic configurations", e);
            return Collections.emptyMap();
        }
    }

    private Properties convertConfigToProperties(Config config) {
        Properties properties = new Properties();
        config.entries().forEach(entry -> {
            if (entry.value() != null) {
                properties.put(entry.name(), entry.value());
            }
        });
        return properties;
    }
}

