import java.net.URI;
import java.net.URISyntaxException;
import java.security.KeyStore;
import java.util.Map;

public class WebSocketTransport extends WebSocketClient {
    private static final int CONNECT_TIMEOUT = 5000;
    private final WebSocketListener webSocketListener;

    public WebSocketTransport(String serverUri, HttpConfiguration.SSLProperties sslProperties, Map<String, String> headers, WebSocketListener webSocketListener) throws URISyntaxException {
        super(new URI(serverUri), headers);
        this.webSocketListener = webSocketListener;
        if (sslProperties != null) {
            try {
                KeyStore keyStore = KeyStore.getInstance(sslProperties.getKeyStoreType());
                keyStore.load(sslProperties.getKeyStoreInputStream(), sslProperties.getKeyStorePassword().toCharArray());
                TrustManagerFactory trustManagerFactory = TrustManagerFactory.getInstance(TrustManagerFactory.getDefaultAlgorithm());
                trustManagerFactory.init(keyStore);
                SSLContext sslContext = SSLContext.getInstance("TLS");
                sslContext.init(null, trustManagerFactory.getTrustManagers(), null);
                setSocketFactory(sslContext.getSocketFactory());
            } catch (Exception e) {
                throw new RuntimeException("Failed to initialize SSL context", e);
            }
        }
        setConnectionLostTimeout(CONNECT_TIMEOUT);
    }

    @Override
    public void onOpen(ServerHandshake serverHandshake) {
        if (webSocketListener != null) {
            webSocketListener.onOpen(serverHandshake);
        }
    }

