@ServerEndpoint("/whiteboard")
public class Whiteboard {
    private static final Logger LOGGER = Logger.getLogger(Whiteboard.class.getName());
    private static final Object PRESENT = new Object();
    private static final ConcurrentMap<Session, Object> peers = new ConcurrentHashMap<>();

    @OnOpen
    public void onOpen(Session peer) {
        peers.put(peer, PRESENT);
        LOGGER.log(Level.INFO, "New connection opened: {0}", peer.getId());
    }

    @OnClose
    public void onClose(Session peer) {
        peers.remove(peer);
        LOGGER.log(Level.INFO, "Connection closed: {0}", peer.getId());
    }

    @OnMessage
    public void broadcastFigure(Figure figure, Session session) {
        peers.keySet().stream()
