            .forEach(peer -> {
                try {
                    peer.getBasicRemote().sendObject(figure);
                } catch (Exception e) {
                    LOGGER.log(Level.SEVERE, "Error broadcasting figure to peer: {0}", peer.getId());
                }
            });
    }

    @OnMessage
    public void broadcastSnapshot(ByteBuffer data, Session session) {
        peers.keySet().stream()
            .filter(peer -> !peer.equals(session))
            .forEach(peer -> {
                try {
                    peer.getBasicRemote().sendBinary(data);
                } catch (Exception e) {
                    LOGGER.log(Level.SEVERE, "Error broadcasting snapshot to peer: {0}", peer.getId());
                }
            });
    }
}

