import javax.websocket.OnMessage;
import javax.websocket.Session;
import javax.websocket.server.ServerEndpoint;
import java.io.IOException;
import java.io.InputStream;
import java.nio.ByteBuffer;

@ServerEndpoint("/inputstream")
public class BinaryEchoEndpoint {

    @OnMessage
    public void echoStream(InputStream stream, Session session) throws IOException {
        byte[] buffer = new byte[12];
        int bytesRead = stream.read(buffer);

        if (bytesRead != -1) {
            System.out.println("Received stream: " + bytesToHex(buffer, bytesRead));
            session.getBasicRemote().sendBinary(ByteBuffer.wrap(buffer, 0, bytesRead));
        }
    }

    private String bytesToHex(byte[] bytes, int length) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < length; i++) {
            sb.append(String.format("%02x", bytes[i]));
        }
        return sb.toString();
    }
}

