import javax.websocket.server.ServerEndpoint;
import java.util.concurrent.ConcurrentHashMap;

@ServerEndpoint("/websocket/{name}")
public class WebSocket {
    private Session session;
    private String name;
    private static ConcurrentHashMap<String, WebSocket> webSocketSet = new ConcurrentHashMap<>();

    @OnOpen
    public void onOpen(Session session, @PathParam(value = "name") String name) {
        this.session = session;
        this.name = name;
        webSocketSet.put(name, this);
        groupSending("User " + name + " has joined the chat.");
    }

    @OnClose
    public void onClose() {
        webSocketSet.remove(this.name);
        groupSending("User " + this.name + " has left the chat.");
    }

    @OnMessage
    public void onMessage(String messageStr) {
        if (messageStr.startsWith("@")) {
            int spaceIndex = messageStr.indexOf(" ");
            if (spaceIndex > 0) {
                String targetName = messageStr.substring(1, spaceIndex);
                String message = messageStr.substring(spaceIndex + 1);
                appointSending(targetName, message);
            }
        } else {
            groupSending(this.name + ": " + messageStr);
        }
    }

    @OnError
    public void onError(Session session, Throwable error) {
        error.printStackTrace();
    }

    private void groupSending(String message) {
        for (WebSocket webSocket : webSocketSet.values()) {
            try {
                webSocket.session.getBasicRemote().sendText(message);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    private void appointSending(String name, String message) {
        WebSocket webSocket = webSocketSet.get(name);
        if (webSocket != null) {
            try {
                webSocket.session.getBasicRemote().sendText(this.name + " (private): " + message);
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }
}

