import javax.websocket.*;
import javax.websocket.server.PathParam;
import javax.websocket.server.ServerEndpoint;
import java.util.Collections;
import java.util.HashMap;
import java.util.Map;
import java.util.logging.Level;
import java.util.logging.Logger;

@ServerEndpoint("/chat/{username}")
public class ChatRoomServerEndpoint {

    private static final Logger logger = Logger.getLogger(ChatRoomServerEndpoint.class.getName());
    private static final Map<String, Session> ONLINE_USER_SESSIONS = Collections.synchronizedMap(new HashMap<>());

    @OnOpen
    public void openSession(@PathParam("username") String username, Session session) {
        ONLINE_USER_SESSIONS.put(username, session);
        broadcastMessage("Server", username + " has joined the chat.");
        logger.log(Level.INFO, "User {0} connected.", username);
    }

    @OnMessage
    public void onMessage(@PathParam("username") String username, String message) {
        broadcastMessage(username, message);
        logger.log(Level.INFO, "Message from {0}: {1}", new Object[]{username, message});
    }

    @OnClose
    public void onClose(@PathParam("username") String username, Session session) {
        ONLINE_USER_SESSIONS.remove(username);
        broadcastMessage("Server", username + " has left the chat.");
        logger.log(Level.INFO, "User {0} disconnected.", username);
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.log(Level.SEVERE, "Error in session {0}: {1}", new Object[]{session.getId(), throwable.getMessage()});
        try {
            session.close();
        } catch (Exception e) {
            logger.log(Level.SEVERE, "Error closing session {0}: {1}", new Object[]{session.getId(), e.getMessage()});
        }
    }

    private void broadcastMessage(String sender, String message) {
        ONLINE_USER_SESSIONS.values().forEach(session -> {
            try {
                session.getBasicRemote().sendText(sender + ": " + message);
            } catch (Exception e) {
                logger.log(Level.SEVERE, "Error sending message to session {0}: {1}", new Object[]{session.getId(), e.getMessage()});
            }
        });
    }
}

